import { Table, Image, Space, Button, Popconfirm, message } from 'antd';
import { Link } from 'react-router-dom';
import { useDeletePostMutation, useFetchTrashQuery, useRestorePostMutation } from '../../../services/post.service';
import { useEffect } from 'react';

const TrashPage = () => {
    const { data, isLoading, refetch } = useFetchTrashQuery()
    const [deletePost] = useDeletePostMutation()
    const [restorePost] = useRestorePostMutation()

    useEffect(() => {
        if(data) {
            refetch()
        }
    }, [window.location.pathname])

    const confirm = (id) => {
        deletePost(id)
        message.success('Post deleted successfully');
    };

    const handleRestore = (id) => {
        restorePost(id)
    }

    const columns = [
        {
            title: 'Title',
            dataIndex: 'title',
            key: 'title',
            render: (_, { title, _id }) => (
                <Link to={'/admin/posts/edit/' + _id}>{title}</Link>
            )
        },
        {
            title: 'SubTitle',
            dataIndex: 'subTitle',
            key: 'subTitle',
        },
        {
            title: 'Likes',
            dataIndex: 'likes',
            key: 'likes',
            render: (_, { likes }) => (
                <p>{likes.length}</p>
            )
        },
        {
            title: 'Comments',
            dataIndex: 'comments',
            key: 'comments',
            render: (_, { comments }) => (
                <p>{comments.length}</p>
            )
        },
        {
            title: 'Thumbnail',
            dataIndex: 'thumbnail',
            key: 'thumbnail',
            render: (_, { thumbnail }) => thumbnail.length > 0 ?
                <Image
                    width={200}
                    height={200}
                    src={thumbnail[0]}
                    fallback="data:image/png;base64,"
                />
                :
                <Image
                    width={200}
                    height={200}
                    src="error"
                    fallback="data:image/png;base64,"
                />
        },
        {
            title: 'Content',
            dataIndex: 'content',
            key: 'content',
            render: (_, record) => (
                <p style={{WebkitLineClamp: '4', wordBreak: 'break-word', overflowWrap: 'break-word',textOverflow: 'ellipsis', overflow: 'hidden', display: '-webkit-box', WebkitBoxOrient: 'vertical'}} dangerouslySetInnerHTML={{ __html: record.content }}></p>
            ),
            width: 300
        },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => (
                <Space size="middle">
                    <Button onClick={() => handleRestore(record._id)}>Restore</Button>
                    <Popconfirm
                        title="Delete post"
                        description="Are you sure to delete this Post?"
                        onConfirm={() => confirm(record._id)}
                        onCancel={() => message.error('Cancel')}
                        okText="Yes"
                        cancelText="No"
                    >
                        <Button>Delete</Button>
                    </Popconfirm>
                </Space>
            ),
        },
    ];
    if (isLoading) {
        return <div>Loading</div>
    }
    return <div>
        <h1>Your trash can</h1>
        <Table columns={columns} dataSource={data?.posts} />
    </div>
}
export default TrashPage;